from django import formsfrom .models import BookCoverTEMPLATE_CHOICES = [    ('classic', 'Классический'),    ('dark', 'Тёмный'),    ('modern', 'Современный'),]class BookCoverForm(forms.ModelForm):    template = forms.ChoiceField(choices=TEMPLATE_CHOICES, widget=forms.Select())    cover_image = forms.CharField(widget=forms.HiddenInput())    class Meta:        model = BookCover        fields = ['title', 'author_book', 'dedication', 'template', 'cover_image']